<?php
if (!isset($_SESSION["name"]) || !isset($_SESSION["fileToOpen"]) || !isset($_SESSION["userDir"]))
{
    header("Location: /adminpanel/admin/");
}
else
{
    $fileToConvert = $_SESSION["fileToOpen"];
    $getFileName = explode("/", $fileToConvert);
    $fileToConvert = $getFileName[3];
    $targetPath = $_SERVER["DOCUMENT_ROOT"] . "/adminpanel/TempDocSave/" . $fileToConvert . $_SESSION["userDir"];
    $sourcePath = $_SERVER["DOCUMENT_ROOT"] . "/adminpanel/UserAttachments/" . $_SESSION["userDir"];
    $convert = "";
    
    if (php_uname("s") == "Windows NT") // Checking OS
    {
      $convert = 'soffice --convert-to odt --outdir ' . $userDir . '  ' . $editedDocDir . '/' . $editedFile[2];
    }
    else
    {
      $convert = 'sudo /usr/bin/soffice --convert-to odt --outdir ' . $userDir . '  ' . $editedDocDir . '/' . $editedFile[0];
    }

    if (!is_dir($targetPath))
    {
      if (!shell_exec($convert))
      {
        die("Hibába csúsztam!");
      }
      else
      {
        if (php_uname("s") != "Windows NT") // Checking OS
        {
          shell_exec("sudo chown -R www-data:www-data /var/www/cegalapitas/adminpanel/TempDocSave");
        }
        $extension = explode(".", $fileToConvert);
        $convertedFile = str_replace($extension[1], "html", $fileToConvert);
        $htmlFile = $targetPath . "/" . $convertedFile;
        $html = file_get_contents($htmlFile);
        $_SESSION["htmlDocName"] = $htmlFile;
        echo "<div style='width: 1500px;'><h1>" . $fileToConvert . "</h1><p>converted</p></div>";
      }
    }
    else
    {
      $extension = explode(".", $fileToConvert);
      $existingFile = $extension[0];
      $htmlFile = $targetPath . "/" . $existingFile . ".html";
      $html = file_get_contents($htmlFile);
      $_SESSION["htmlDocName"] = $htmlFile;
      echo "<div style='width: 1500px;'><h1>" . $fileToConvert . "</h1><p>existing</p></div>";
    }
}
?>
<script>
var oDoc, sDefTxt;

function initDoc()
{
  oDoc = document.getElementById("textBox");
  sDefTxt = oDoc.innerHTML;
}

function formatDoc(sCmd, sValue)
{
  if (validateMode())
  {
    document.execCommand(sCmd, false, sValue); oDoc.focus();
  }
}

function validateMode()
{
  return true;
}

function printDoc()
{
  if (!validateMode())
  {
    return;
  }

  var oPrntWin = window.open("","_blank","width=600,height=600,left=0,top=100,menubar=yes,toolbar=no,location=no,scrollbars=yes");
  oPrntWin.document.open();
  oPrntWin.document.write("<!doctype html><html><head><title>Print<\/title><\/head><body onload=\"print();\">" + oDoc.innerHTML + "<\/body><\/html>");
  oPrntWin.document.close();
}
</script>
<body onload="initDoc();">
<form id="editor" name="compForm">
    <input type="hidden" id="myDoc" name="myDoc">
    <div id="toolBar">
